    mC_B()
mC_Bnat_num_t< UINT_T, B >
nat_num_t< UINT_T, B >mC_B
template<type_traits::unsigned_integral_c UINT_T, UINT_T B> 
const nat_num_t & nat_num_t< UINT_T, B >::mC_B ( )[inline]01343                           {
01344         nat_num_t & cpy = (*this);
01345         cpy.reduce();
01346         crstriterator start = cpy.rend();
01347         rstriterator  it    = cpy.rbegin();
01348         for ( ; it != start ; ++it ) {
01349                 (*it) = (!(*it));
01350         }
01351         cpy.reduce();
01352         it = cpy.rbegin();
01353         cpy.aux.p_uds(*it);
01354         cpy.aux.p_decs(dig());
01355         cpy.aux += pd_1<B>();
01356         (*it) = cpy.aux.g_uds();
01357         ++it;
01358         if ((cpy.aux.g_decs())!= dig(0)) {
01359                 for ( ; it != start ; ++it ) {
01360                         cpy.aux.p_uds(cpy.aux.g_decs());
01361                         cpy.aux.p_decs(0);
01362 
01363                         if (cpy.aux == pardigs()) break;
01364                         cpy.aux += pardigs(*it);
01365                         (*it) = cpy.aux.g_uds();
01366                 }
01367                 cpy.push_front(cpy.aux.g_uds());
01368                 cpy.push_front(cpy.aux.g_decs());
01369         }
01370 
01371         cpy.aux = pardigs();
01372         cpy.reduce();
01373         return cpy;
01374 }


