    minus()
minusnat_num_t< UINT_T, B >
nat_num_t< UINT_T, B >minus
template<type_traits::unsigned_integral_c UINT_T, UINT_T B> 
nat_num_t nat_num_t< UINT_T, B >::minus (size_t sz) const[inline]01273                                   {
01274         nat_num_t cpy(*this);
01275         cpy.reduce();
01276         if (cpy.size() < sz) {
01277                 const int tm = sz - cpy.size();
01278                 for(int i=0; i<tm ; ++i) {
01279                         cpy.push_front(dig(0));
01280                 }
01281         }
01282         else if (cpy.size() > sz) {
01283                 cpy.erase(0,1);
01284         }
01285         crstriterator start = cpy.rend();
01286         rstriterator  it    = cpy.rbegin();
01287         for ( ; it != start ; ++it ) {
01288                 (*it) = (!(*it));
01289         }
01290         it = cpy.rbegin();
01291         cpy.aux.p_uds(*it);
01292         cpy.aux.p_decs(dig(0));
01293         cpy.aux += pd_1<B>();
01294         (*it) = cpy.aux.g_uds();
01295         ++it;
01296         if ((cpy.aux.g_decs())!= dig(0)) {
01297                 for ( ; it != start ; ++it ) {
01298                         cpy.aux.p_uds(cpy.aux.g_decs());
01299                         cpy.aux.p_decs(0);
01300 
01301                         if (cpy.aux == pardigs()) break;
01302                         cpy.aux += pardigs(*it);
01303                         (*it) = cpy.aux.g_uds();
01304                 }
01305                 cpy.push_front(cpy.aux.g_uds());
01306                 cpy.push_front(cpy.aux.g_decs());
01307         }
01308 
01309         cpy.aux = pardigs();
01310         if (cpy.size() < sz) {
01311                 const int tm = sz - cpy.size();
01312                 for(int i=0; i<tm ; ++i) {
01313                         cpy.push_front(dig(B-1));
01314                 }
01315         }
01316         else if (cpy.size() > sz) {
01317                 cpy.erase(0,1);
01318         }
01319         //cpy.reduce();
01320         return cpy;
01321 }


