Hace referencia a nat_num_t< UINT_T, B >::minus(), nat_num_t< UINT_T, B >::reduce() y nat_num_t< UINT_T, B >::size().
Gráfico de llamadas para esta función:












    operator-=()[2/2]
operator-=nat_num_t< UINT_T, B >
nat_num_t< UINT_T, B >operator-=
template<type_traits::unsigned_integral_c UINT_T, UINT_T B> 
const nat_num_t & nat_num_t< UINT_T, B >::operator-= (dig arg)[inline]01184                                         {
01185         nat_num_t & cthis = (*this);
01186         cthis.reduce();
01187         if (arg == dig(0)) return cthis;
01188         nat_num_t cpy(nat_num_t(arg).minus(cthis.size()));
01189         pardigs tempt;
01190         pardigs tempa;
01191         sint szt = cthis.size();
01192         sint k = szt-1;
01193         tempt.p_uds(cthis[k]);
01194         tempt.p_decs(dig(0));
01195         tempa.p_uds(cpy[k]);
01196         tempa.p_decs(dig(0));
01197         tempt += tempa;
01198         cthis[k] =tempt.g_uds();
01199         dig carry(tempt.g_decs());
01200         --k;
01201         for ( ; k >= 0 ; --k) {
01202                 tempt.p_uds(cthis[k]);
01203                 tempt += cpy[k];
01204                 tempt += carry;
01205                 carry =tempt.g_decs();
01206                 cthis[k] =tempt.g_uds();
01207         }
01208 
01209         cthis.reduce();
01210         cthis.aux =  pardigs();
01211         return cthis;
01212 }


