nat_num_t nat_num_t< UINT_T, B >::operator/ (const nat_num_t< UINT_T, B > & arg) const[inline]00791                                                    {
00792         const nat_num_t & cthis = (*this);
00793         nat_num_t dvndo_int(cthis); //Siempre será positivo
00794         dvndo_int.reduce();
00795         nat_num_t rem(dvndo_int);
00796         rem.reduce();
00797         nat_num_t dvsor_int(arg);//Siempre será positivo
00798         dvsor_int.reduce();
00799 
00800         const usint cssor = dvsor_int.ceros_a_la_drcha(); // ceros que tienen valor y por los que dividir
00801         // divisor y dividendo
00802         nat_num_t dvndo_fra(dvndo_int);
00803 
00804         if (dvndo_int.size()>=dvsor_int.size()) {
00805                 dvndo_fra.erase(0,dvndo_int.size()-cssor);
00806                 if (dvndo_int != nat_num_t(dig(0))) {
00807                         dvndo_int.erase(dvndo_int.size()-cssor,cssor);// division por una potencia de B, /B^cssor
00808                 }
00809                 dvndo_int.reduce();
00810                 if (dvsor_int != nat_num_t(dig(0))) {
00811                         dvsor_int.erase(dvsor_int.size()-cssor,cssor);// division por una potencia de B  /B^cssor
00812                 }
00813                 nat_num_t dvsor_fra(dvsor_int);
00814                 dvsor_fra.erase(0,dvsor_int.size()-cssor);
00815                 dvsor_int.reduce();
00816         }
00817         else {
00818                 dvndo_int = nat_num_t(dig(0));
00819                 return dvndo_int;
00820         }
00821         //dvndo_fra.reduce();//dvndo_fra.reduce_fra() HAY QUE HACERLO
00822         const int szndo         = dvndo_int.size();
00823         const int lsb_ndo   = szndo-1;
00824         const int msb_ndo       = 0;
00825         const int szsor     = dvsor_int.size();
00826         const int lsb_sor   = szsor-1;
00827         const int msb_sor   = 0;
00828 
00829         // hemos dividido por los ceros del divisor (por una potencia de B)
00830         // tanto divisor como dividendo, obteniendo dvndo_int y dvsor_int como
00831         // cocientes enteros.
00832         div_uno(rem,dvndo_int,dvsor_int,szndo,szsor);
00833         // ahora hacemos lo mismo con el cociente de las potencias de B del
00834         // dividendo obteniendo dvndo_fra
00835 
00836         /* *
00837                 RemReal = RemObtenido*(B^cssor) + Dvndo_fra
00838 
00839                 rem = rem.insert(rem.size()-1,cssor,0);
00840                 rem += dvndo_fra;
00841         * */
00843         // rehacer el resto tomando en cuenta los ceros quitados
00844         // acomodar el formato al signo de la operacion
00845         // determinar el signo de la operacion
00846         return dvndo_int;
00847 }


