template<type_traits::allowable_base_type_c UINT_T, UINT_T B> requires (type_traits::suitable_base<UINT_T,B>())
void NumRepr::display_operator_C_B ( )01133                             {
01134         using base_B_dig_t = dig_t<UINT_T,B>;
01135         constexpr std::int64_t int64_B{B};
01136         std::uint64_t contador_buenos{};
01137         std::uint64_t contador_malos{};
01138         std::uint64_t contador{};
01139         for(std::int64_t nd{-2*int64_B} ; nd < 2*int64_B ; ++nd) {
01140                         std::int64_t nnd{nd};
01141                         while (nnd < 0)
01142                                 nnd += int64_B;
01143                         while (nnd >= int64_B)
01144                                 nnd -= int64_B;
01145 
01146                         std::int64_t result_int{int64_B-nnd};
01147                         while (result_int < 0)
01148                                 result_int += int64_B;
01149                         while (result_int >= int64_B)
01150                                 result_int -= int64_B;
01151 
01152                         base_B_dig_t uds{nnd};
01153                         base_B_dig_t result_dig{uds.C_B()};
01154 
01155                         if (result_dig.get() == result_int)
01156                                 ++contador_buenos;
01157                         else
01158                                 ++contador_malos;
01159 
01160                         ++contador;
01161         }
01162         std::cout       << "___________________________________________"<< std::endl;
01163         std::cout       << "__BEGIN___TESTS_COMPLEMENTO_BASE___BEGIN___"<< std::endl;
01164         std::cout       << " Numero de complementos a la base correctas = "
01165                                 << contador_buenos                                                              << std::endl;
01166         std::cout       << " Numero de complementos a la base fallidos  = "
01167                                 << contador_malos                                                               << std::endl;
