00606         std::uint32_t contador_buenos{};
00607         std::uint32_t contador_malos{};
00608         std::uint32_t contador{};
00609         for(std::int32_t nd{1-int32_B} ; nd < int32_B ; ++nd) {
00610                 if (nd != 0) {
00611                         for(std::int32_t md{1-int32_B} ; md < int32_B ; ++md) {
00612                                 std::int32_t mmd{md};
00613                                 std::int32_t nnd{nd};
00614                                 base_B_dig_t uds{mmd};
00615 
