00627                                         mmd += B;
00628                                 }
00629                                 while (mmd>=int32_B) {
00630                                         mmd -= B;
00631                                 }
00632 
00633                                 if (uds.get() == mmd) {
00634                                         ++contador_buenos;
00635                                 }
00636                                 else {
00637                                         ++contador_malos;
00638                                 }
00639 
00640                                 ++contador;
00641                         }
