01019                                   {
01020         using base_B_dig_t = dig_t<UINT_T,B>;
01021         constexpr std::int64_t int64_B{B};
01022         std::uint64_t contador_buenos{};
01023         std::uint64_t contador_malos{};
01024         std::uint64_t contador{};
01025         for(std::int64_t nd{-1} ; nd < int64_B ; ++nd) {
01026                 if (nd%B != 0) {
01027                         for(std::int64_t md{-1} ; md < int64_B ; ++md) {
01028 
01029                                 base_B_dig_t nos{nd};
01030                                 base_B_dig_t uds{md};
01031                                 base_B_dig_t result_dig{uds/nos};
01032                                 base_B_dig_t prueba_ediv_dig{uds-(result_dig*nos)};
01033 
01034                                 std::int64_t mmd{md};
01035                                 while (mmd < 0)
01036                                         mmd += int64_B;
01037                                 while (mmd >= int64_B)
01038                                         mmd -= int64_B;
01039                                 std::int64_t nnd{nd};
01040                                 while (nnd < 0)
01041                                         nnd += int64_B;
01042                                 while (nnd >= int64_B)
01043                                         nnd -= int64_B;
01044                                 std::int64_t result_int{mmd/nnd};
01045                                 while (result_int < 0)
01046                                         result_int += int64_B;
01047                                 while (result_int >= int64_B)
01048                                         result_int -= int64_B;
01049                                 std::int64_t prueba_ediv_int{mmd-(result_int*nnd)};
01050                                 while (prueba_ediv_int < 0)
01051                                         prueba_ediv_int += int64_B;
01052                                 while (prueba_ediv_int >= int64_B)
01053                                         prueba_ediv_int -= int64_B;
01054                                 if (prueba_ediv_dig.get() == prueba_ediv_int) {
01055                                         ++contador_buenos;
01056                                 }
01057                                 else {
01058                                         ++contador_malos;
01059                                 }
01060                                 ++contador;
01061                         }
01062                 }
01063         }
01064         std::cout       << "___________________________________________"<< std::endl;
