00718         template<type_traits::integral_c Int_t>
00719         constexpr inline const dig_t & operator *=(Int_t arg)  noexcept
00720         {
00721                 const Int_t tmp{normaliza<Int_t>(arg)};
00722                 if constexpr (std::is_signed_v<Int_t>)
00723                 {
00724                         if constexpr (sizeof(Int_t)>sizeof(UINT_T))
00725                         {
00726                                 using SIG2_SINT_T = type_traits::sig_SInt_for_SInt_t<Int_t>;
00727                                 const SIG2_SINT_T norm_arg{tmp};
00728                                 SIG2_SINT_T este{m_d};
00729                                 este *= norm_arg;
00730                                 este %= static_cast<SIG2_SINT_T>(B);
00731                                 m_d = static_cast<UINT_T>(este);
00732                                 return (*this);
00733                         }
00734                         else
00735                         {
00736                                 const SIG_SINT_T norm_arg{tmp};
00737                                 SIG_SINT_T este{m_d};
00738                                 este *= norm_arg;
00739                                 este %= static_cast<SIG_SINT_T>(B);
00740                                 m_d = static_cast<UINT_T>(este);
00741                                 return (*this);
00742                         }
00743                 }
00744                 else
00745                 {
00746                         if constexpr (sizeof(Int_t)>sizeof(UINT_T))
00747                         {
00748                                 using SIG2_UINT_T = type_traits::sig_UInt_for_UInt_t<Int_t>;
00749                                 const SIG2_UINT_T norm_arg{tmp};
00750                                 SIG2_UINT_T este{m_d};
00751                                 este *= norm_arg;
00752                                 este %= static_cast<SIG2_UINT_T>(B);
00753                                 m_d = static_cast<UINT_T>(este);
00754                                 return (*this);
00755                         }
00756                         else
00757                         {
00758                                 const SIG_UINT_T norm_arg{tmp};
00759                                 SIG_UINT_T este{m_d};
00760                                 este *= norm_arg;
00761                                 este %= static_cast<SIG_UINT_T>(B);
00762                                 m_d = static_cast<UINT_T>(este);
00763                                 return (*this);
00764                         }
00765                 }
00766         }
