01187     std::string new_input_string{""};
01188     size_t index{ 0 };
01189     while(true) {
01190       is >> input_char;
01191       new_input_string += input_char;
01192       if (is_type_template_string_id(new_input_string)) {
01193         old_input_string = new_input_string;
01194       }
01195       else {
01196         std::cerr << "Has cometido un error, tenias que escribir "
01197         << "\" " << to_type_template_string_id()
