01312   static constexpr
01313   bool get_digit_token(std::istream& is,UINT_T& uint_value)
01314   {
01315     std::string old_input_string{""};
01316     std::string new_input_string{""};
01317     size_t index{0};
01318     char input_char = type_traits::nullchar<char>;
01319     uint_value = 0;
01320     while(true) {
01321       is >> input_char;
01322       if ((index==0)&&(NumRepr::lex::is_separator(input_char))) {
01323         new_input_string += input_char;
01324         old_input_string = new_input_string;
01325         ++index;
01326       }
01327       else if ((index == 0)&&(! lex::is_separator(input_char))) {
01328         std::cerr << "Has cometido un error, tenias que "
01329         << " escribir \" " << '#' << " \"  y has escrito "
01330         << new_input_string << std::endl;
01331         std::cerr << "Considera que has escrito "
01332         << old_input_string
01333         << " y continua escribiendo a partir de ahi";
01334         new_input_string = old_input_string;
01335       }
01336       else if((index>=1) && lex::is_digit(input_char)) {
01337         if (lex::digit_value(input_char) < B) {
01338           new_input_string += input_char;
01339           uint_value *= B;
01340           uint_value += lex::digit_value(input_char);
01341           if (uint_value < B) {
01342             old_input_string = new_input_string;
01343             ++index;
01344           }
01345           else {
01346             old_input_string = "#";
01347             std::cerr << "Has cometido un error, tenias que "
01348             << "poner un valor menor que la base y has "
01349             << "puesto \" " << uint_value << " \" escrito como \" "
01350             << new_input_string << " \" " << std::endl;
01351             uint_value = 0;
01352             return false;
01353           }
01354         }
01355         else if((index>=1) && ! lex::is_digit(input_char) && (input_char != '_')) {
01356           std::cerr << "El caracter " << input_char << " no es valido aqui "
01357           << std::endl;
01358           uint_value = 0;
01359           return false;
01360         }
01361       }
01362       else if((index>1)&&(input_char == '_')) {
01363         return true;
01364       }
01365       else{
01366         std::cerr << "Has cometido un error, tenias que escribir "
01367         << " \" " << "#digdigdig..._"
01368         << " \"  y has escrito "
01369         << new_input_string << std::endl;
01370         uint_value = 0;
01371         return false;
01372       }
01373     }
01374   }
