01377   static
01378   bool get_radix_token(std::istream& is)
01379   {
01380     std::string old_input_string{""};
01381     std::string new_input_string{""};
01382     UINT_T uint_radix;
01383     size_t index{0};
01384     char input_char = type_traits::nullchar<char>;
01385     uint_radix = 0;
01386     while(true) {
01387       is >> input_char;
01388       if ((index==0)&&(lex::is_separator(input_char))) {
01389         new_input_string += input_char;
01390         old_input_string = new_input_string;
01391         ++index;
01392       }
01393       else if ((index == 0)&&(! lex::is_separator(input_char))) {
01394         std::cerr << "Has cometido un error, tenias que "
01395         << " escribir \" " << '#' << " \"  y has escrito "
01396         << new_input_string << std::endl;
01397         std::cerr << "Considera que has escrito "
01398         << old_input_string
01399         << " y continua escribiendo a partir de ahi";
01400         new_input_string = old_input_string;
01401       }
01402       else if ((index==1)&&((input_char=='b')||(input_char=='B'))) {
01403         new_input_string += input_char;
01404         old_input_string = new_input_string;
01405         ++index;
01406       }
01407       else if ((index == 1)&&((input_char!='b')&&(input_char!='B'))) {
01408         std::cerr << "Has cometido un error, tenias que "
01409         << " escribir \" " << '#' << " \"  y has escrito "
01410         << new_input_string << std::endl;
01411         std::cerr << "Considera que has escrito "
01412         << old_input_string
01413         << " y continua escribiendo a partir de ahi";
01414         new_input_string = old_input_string;
01415       }
01416       else if((index>=2) && lex::is_digit(input_char)) {
01417         if (lex::digit_value(input_char) <= B) {
01418           new_input_string += input_char;
01419           uint_radix *= B;
01420           uint_radix += lex::digit_value(input_char);
01421           if (uint_radix <= B) {
01422             old_input_string = new_input_string;
01423             ++index;
01424           }
01425           else {
01426             old_input_string = "#";
01427             std::cerr << "Has cometido un error, tenias que "
01428             << "poner un valor menor que la base y has "
01429             << "puesto \" " << uint_radix << " \" escrito como \" "
01430             << new_input_string << " \" " << std::endl;
01431             uint_radix = 0;
01432             return false;
01433           }
01434         }
01435         else if((index>=3) && ! lex::is_digit(input_char) && (input_char != '_')) {
01436           std::cerr << "El caracter " << input_char << " no es valido aqui "
01437           << std::endl;
01438           uint_radix = 0;
01439           return false;
01440         }
01441       }
01442       else if((index>2)&&(input_char == '_')) {
01443         return true;
01444       }
01445       else{
01446         std::cerr << "Has cometido un error, tenias que escribir "
01447         << " \" " << "#Bdigdigdig..._"
01448         << " \"  y has escrito "
01449         << new_input_string << std::endl;
01450         uint_radix = 0;
01451         return false;
01452       }
01453     }
01454   }
01455 
01456         static
01457         bool read(std::istream& is,dig_t& value) noexcept {
01458                 dig_t default_value{};
01459                 bool type_token = dig_t::get_type_template_string_id_token(is);
01460                 if (type_token) {
01461                         if (get_digit_token(is,value)) {
01462                                 if (get_radix_token(is)) {
01463                                         return true;
01464                                 }
01465                                 else {
01466                                         value = default_value;
01467                                         return false;
01468                                 }
01469                         }
01470                         else {
01471                                 value = default_value;
01472                                 return false;
01473                         }
01474                 } else {
01475                         value = default_value;
01476                         return false;
01477                 }
01478         }
01479 
01480 };
