01493 template<uint128_t B>
01494 using digit_t = dig_t<
01495                 type_traits::TypeFromIntNumber_t<static_cast<uint128_t>(B)>,
01496                 static_cast<type_traits::TypeFromIntNumber_t<static_cast<uint128_t>(B)>>(static_cast<uint128_t>(B))
01497         >;
