01505 
01506 template<type_traits::uint_type_for_radix_c UINT_T,UINT_T Base>
01507         requires (type_traits::suitable_base<UINT_T,Base>())
01508 std::istream & operator >> (std::istream & is,dig_t<UINT_T,Base> & arg) {
01509 
01510         using SIG_UINT_T        = type_traits::sig_UInt_for_UInt_t<UINT_T>;
01511         enum estado_e {e0,e1,e2,e3,e4,e5,e6,e7,e8};
01512 
01513         std::string sds;
01514         std::string num_dig;
01515         std::string num_base;
01516 
01517         estado_e est_act = e0;
01518         SIG_UINT_T indice = 0;
01519         SIG_UINT_T numero_base_recogido=0;
01520         UINT_T numero = 0;
01521 
01522         char c;
01523         is >> sds;
01524         do {
01525                 c = sds[indice];
01526                 switch(est_act) {
01527                         case e0 : if (c=='d'){
01528                                         est_act = e1;
01529                                 }
01530                                 break;
01531                         case e1 : if (c=='#') {
01532                                         est_act = e2;
01533                                 }
01534                                 else {
01535                                         est_act = e0;
01536                                 }
01537                                 break;
01538                         case e2 : if ((c<='9')and(c>='0')) {
01539                                         est_act = e3;
01540                                         num_dig.push_back(c-'0');
01541                                 }
01542                                 else {
01543                                         est_act = e0;
01544                                         num_dig.clear();
01545                                 }
01546                                 break;
01547                         case e3 : if ((c<='9')and(c>='0')) num_dig.push_back(c-'0');
01548                                           else if (c == '#') est_act = e4;
01549                                           else {
01550                                                 est_act=e0;
01551                                                 num_dig.clear();
01552                                           }
01553                                           break;
01554                         case e4 : if (c == 'B') est_act = e5;
01555                                           else {
01556                                                 est_act = e0;
01557                                                 num_dig.clear();
01558                                           }
01559                                           break;
01560                         case e5 : if ((c<='9')and(c>='0')) {
01561                                                 est_act = e6;
01562                                                 num_base.push_back(c-'0');
01563                                           }
01564                                           else {
01565                                                 est_act = e0;
01566                                                 num_dig.clear();
01567                                                 num_base.clear();
01568                                           }
01569                                           break;
01570                         case e6 : if ((c<='9')and(c>='0')) num_base.push_back(c-'0');
01571                                           else if ((c==' ')||(c=='\0')) est_act = e7;
01572                                           break;
01573                         case e7 : if ((c!=' ')||(c!='\0')){
01574                                                 est_act = e0;
01575                                                 num_dig.clear();
01576                                                 num_base.clear();
01577                                                 }
01578                                           break;
01579                         case e8 :
01580                         default : break;
01581                 }
01582                 ++indice;
01583                 if (est_act==e7) {
01584                         for (SIG_UINT_T k=0 ; k < num_base.size() ; ++k ) {
01585                                 numero_base_recogido *= 10;
01586                                 numero_base_recogido += num_base[k];
01587                         }
01588                         if (numero_base_recogido!=Base) {
01589                                 est_act = e0;
01590                                 num_dig.clear();
01591                                 num_base.clear();
01592                         }
01593                         else est_act = e8;
01594                 }
01595         } while (est_act!=e8);
01596 
01597         for (SIG_UINT_T k=0 ; k < num_dig.size() ; ++k ) {
01598                 numero *= 10;
01599                 numero += num_dig[k];
01600         }
01601         numero %= Base;
01602         arg = dig_t<UINT_T,Base>(numero);
01603         return is;
01604 }
