01608 std::ostream & operator << (std::ostream & os,dig_t<UINT_T,Base> arg)   {
01609         os  << "d#"
01610                 << static_cast<std::int64_t>(arg()%Base)
01611                 << "#B"
01612                 << static_cast<std::int64_t>(Base) ;
01613         return os;
01614 }
