00332                 template<typename Int_T>
00333         num_int(Int_T a) {
00334             num_int & cthis = *this;
00335             cthis.clear();
00336                         const ullint longitud = num_digs_max_base_B_en_Int_T<Int_T,B>();
00337             cthis.resize(longitud);
00338                         uint indice = 0;
00339             if (a>=0) {
00340                                 for(int i=0;i<longitud;i++) cthis[i]=dig(0);
00341                 signo=vplus;
00342                 ullint rem=0,coc=a;
00343                 while (coc >= B) {
00344                     rem = coc%B;
00345                     cthis[indice] = UInt2Dig<B>(rem);
00346                                         indice++;
00347                     coc = coc/B;
00348                 }
00349                 rem = coc%B;
00350                 cthis[indice] = UInt2Dig<B>(rem);
00351                                 indice++;
00352             }
00353             else {
00354                                 for(int i=0;i<longitud;i++) cthis[i]=dig(B-1);
00355                 signo=vminus;
00356                 long long int rem=0,coc=-a;
00357                 while (coc >= B) {
00358                     rem = coc%B;
00359                     cthis[indice] = !(Int2Dig<B>(rem));
00360                                         indice++;
00361                     coc = coc/B;
00362                 }
00363                 cthis[indice] = !(Int2Dig<B>(coc));
00364                                 ++cthis;
00365                                 //cthis.signo = vplus;
00366                                 //cthis.reduce();
00367                                 cthis.signo = vminus;
00368             }
00369                         cthis.reduce();
00370         }
