00511 usint ceros_a_la_izqda () const {
00512         const num_int & cthis = (*this);
00513         //digitos no significativos para números positivos
00514         bool Exit = false;
00515         int sz=0;
00516         const size_t fin= this->size();
00517         for ( int i=fin-1 ; (i >= 0) and (!Exit) ; --i) {
00518                 bool char_es_0 = (cthis[i]==0);
00519                 if (char_es_0){
00520                         ++sz;
00521                 }
00522                 else Exit=true;
00523         }
00524         return sz;
00525 }
00526 //digitos no significativos para números positivos
00527 
00528 usint unos_a_la_izqda () const {
00529         const num_int & cthis = (*this);
00530         //digitos no significativos para números negativos
00531         bool Exit = false;
00532         int sz=0;
00533         const size_t fin = cthis.size();
00534         for ( int i=fin-1 ; (i >= 0) and (!Exit) ; --i) {
00535                 bool char_es_max = (cthis[i]==(dig_t<B>::max_dig()));
00536                 if (char_es_max){
00537                         ++sz;
00538                 }
00539                 else Exit=true;
00540         }
00541         return sz;
00542 }
00543 //digitos no significativos para números negativos
00544 
00545 size_t ceros_a_la_drcha () const {
00546         //const num_uint & cthis = (*this);
00547         bool Exit = false;
00548         size_t sz=0;
00549         const size_t fin=this->size();
00550         const size_t finl = fin-1;
00551         if (this->operator[](finl)==0) {
00552                 for ( int i=finl ; (i >=0) and (!Exit) ; --i) {
00553                         if (this->operator[](i)==0) ++sz;
00554                         else Exit=true;
00555                 }
00556         }
00557         return sz;
00558 }
