00612                 //digitos no significativos para números positivos fraccionaria
00613         usint Bm1_a_la_izqda () const {
00614             num_int & cthis = *this;
00615             //digitos no significativos para números negativos
00616             bool Exit = false;
00617             usint sz=0;
00618             usint k=(cthis.size()-1);
00619             for ( ; (k > 0) and (!Exit) ; --k) {
00620                 if (cthis[k]==(B-1)) ++sz;
00621                 else Exit=true;
00622             }
00623             if ((k==0) and (cthis[0]==(B-1)) and (!Exit)) ++sz;
00624             return sz;
00625         }
