00627                 usint Bm1_a_la_drcha () const {
00628             num_int & cthis = *this;
00629             // digitos no significativos para números negativos fraccionarios
00630                         const size_t fin = cthis.size();
00631             bool Exit = false;
00632             usint sz=0;
00633             usint k=0;
00634             for ( ; (k < fin) and (!Exit) ; ++k) {
00635                 if (cthis[k]==(B-1)) ++sz;
00636                 else Exit=true;
00637             }
00638             return sz;
00639         }
