00647         bool operator == (const num_int & arg) const {
00648             const num_int & cthis = *this;
00649             const slint thisnsz = cthis.digs_no_significativos();
00650             const slint thisvsz = cthis.size()-thisnsz;
00651             const slint argnsz = arg.digs_no_significativos();
00652             const slint argvsz = arg.size()-argnsz;
00653             if ((thisvsz > argvsz)||(thisvsz < argvsz)||(signo!=arg.signo)) return false;
00654             else {
00655                 for (slint k=(thisvsz-1);k>0;--k) {
00656                     if (arg[k]!=cthis[k]) return false;
00657                 }
00658                 if ((arg[0])!=(cthis[0])) return false;
00659                 else return true;
00660             }
00661         }
00662 
00663         bool operator != (const num_int & arg) const {
00664             const num_int & cthis = *this;
00665             const slint thisnsz = digs_no_significativos();
00666             const slint thisvsz = (cthis.size())-thisnsz;
00667             const slint argnsz = arg.digs_no_significativos();
00668             const slint argvsz = arg.size()-argnsz;
00669             if ((thisvsz > argvsz)||(thisvsz < argvsz)||(signo!=arg.signo)) return true;
00670             else {
00671                 for (slint k=(thisvsz-1);k>0;--k)
00672                     if (arg[k]!=cthis[k])
00673                         return true;
00674                 if (arg[0]!= cthis[0])
00675                     return true;
00676                 else return false;
00677             }
00678         }
