00680         bool operator >= (const num_int & arg) const {
00681             const num_int & cthis = *this;
00682                         const slint thissz  = cthis.size();
00683             const slint thisnsz = ((thissz==1)?0:(cthis.digs_no_significativos()));
00684                         //cout << "J vale   " << cthis << endl;
00685             const slint thisvsz = thissz-thisnsz;
00686                         const slint argsz  = arg.size();
00687             const slint argnsz = ((argsz==1)?0:(arg.digs_no_significativos()));
00688                         //cout << "I vale   " << arg << endl;
00689             const slint argvsz = argsz-argnsz;
00690 
00691             if ((signo == vplus)and(arg.signo == vminus)) return true;
00692             else if ((signo == vminus)and(arg.signo == vplus)) return false;
00693             else if ((signo == vminus)and(arg.signo == vminus)){
00694                 if (thisvsz < argvsz) return true;
00695                 else if (thisvsz > argvsz) return false;
00696             }
00697                         else if ((signo == vplus)and(arg.signo == vplus)){
00698                 if (thisvsz > argvsz) return true;
00699                 else if (thisvsz < argvsz) return false;
00700             }
00701             for (slint k=(thisvsz-1);k>-1;--k) {
00702                 if ((cthis[k] > arg[k])) return true;
00703                 else if ((cthis[k] < arg[k])) return false;
00704             }
00705             return true;
