00799         num_int operator + (const num_int & arg) const {
00800             const num_int & cthis = *this;
00801             num_int ret(cthis);
00802             ret.reduce();
00803             num_int cpy(arg);
00804             cpy.reduce();
00805             spardigs_t<B> tempt();
00806             spardigs_t<B> tempa();
00807             const num_int* po =0;
00808             usint sza = arg.size();
00809             usint szt = ret.size();
00810             p_usint  pszmin=0;
00811             p_usint  pszmax=0;
00812             if (sza<=szt) {
00813                 pszmin = &sza;
00814                 pszmax = &szt;
00815                 po     = &ret;
00816             } else {
00817                 pszmin = &szt;
00818                 pszmax = &sza;
00819                 po     = &cpy;
00820             }
00821 
00822             usint & szmin = *pszmin;
00823             usint & szmax = *pszmax;
00824             const num_int & obj = *po;
00825             usint k = 0;
00826             ret.aux = spardigs();
00827             for ( ; k<szmin ; ++k) {
00828                 tempt.p_uds(ret[k]);
00829                 tempa.p_uds(arg[k]);
00830                 tempt += tempa;
00831                 tempt += aux;
00832                 ret[k]=tempt.g_uds();
00833                 ret.aux=spardigs(tempt.g_sign(),
00834                                  ((tempt.g_sign()==vplus)?(0):(B-1)),
00835                                  tempt.g_decs()
00836                                 );
00837             }
00838             for ( ; k<szmax ; ++k) {
00839                 tempt.p_first(obj[k]);
00840                 tempt += aux;
00841                 ret[k]=tempt.g_first();
00842                 ret.aux=spardigs(tempt.g_sign(),
00843                                  ((tempt.g_sign()==vplus)?(0):(B-1)),
00844                                  tempt.g_decs()
00845                                 );
00846             }
00847 
00848             ret.push_back(ret.aux.g_uds());
00849             ret.push_back(ret.aux.g_decs());
00850             ret.signo=ret.aux.g_sign();
00851             ret.aux =  spardigs();
00852             return ret;
00853         }
