00855         num_int operator - (const num_int & arg) const {
00856             num_int & cthis = *this;
00857             num_int ret;
00858             num_int cpy(-arg);
00859             ret.reduce();
00860             cpy.reduce();
00861             spardigs tempt();
00862             spardigs tempa();
00863             const num_int* po =0;
00864             usint sza = arg.lst_arg.size();
00865             usint szt = ret.lst_arg.size();
00866             p_usint  pszmin=0;
00867             p_usint  pszmax=0;
00868             if (sza<=szt) {
00869                 pszmin = &sza;
00870                 pszmax = &szt;
00871                 po     = &ret;
00872             } else {
00873                 pszmin = &szt;
00874                 pszmax = &sza;
00875                 po     = &cpy;
00876             }
00877 
00878             usint & szmin = *pszmin;
00879             usint & szmax = *pszmax;
00880             const num_int & obj = *po;
00881             usint k = 0;
00882             ret.aux = spardigs();
00883             for ( ; k<szmin ; ++k) {
00884                 tempt.p_first(ret[k]);
00885                 tempa.p_first(arg[k]);
00886                 tempt += tempa;
00887                 tempt += aux;
00888                 ret[k]=tempt.g_first();
00889                 ret.aux=spardigs(tempt.g_sign(),
00890                                  ((tempt.g_sign()==vplus)?(0):(B-1)),
00891                                  tempt.g_second()
00892                                 );
00893             }
00894             for ( ; k<szmax ; ++k) {
00895                 tempt.p_first(obj[k]);
00896                 tempt += aux;
00897                 ret[k]=tempt.g_first();
00898                 ret.aux=spardigs(tempt.g_sign(),
00899                                  ((tempt.g_sign()==vplus)?(0):(B-1)),
00900                                  tempt.g_second()
00901                                 );
00902             }
00903 
00904             ret.lst_digs.push_back(ret.aux.g_first());
00905             ret.lst_digs.push_back(ret.aux.g_second());
00906             ret.signo=ret.aux.g_sign();
00907             ret.aux =  spardigs();
00908             return ret;
00909         }
