00911         // Utilizamos una funcion num_int * dig_t
00912         num_int operator * (const num_int & arg) const {
00913             num_int & cthis = *this;
00914             num_int sumatemp;
00915             num_int multtemp;
00916             num_int m1(cthis.abs());
00917             m1.reduce();
00918             num_int m2(arg.abs());
00919             m2.reduce();
00920             multtemp = cthis*m2[0];
00921             sumatemp = multtemp;
00922             for (usint j = 1 ; j < m2.size() ; ++j) {
00923                 multtemp = cthis*m2[j]; // num_int * dig_t
00924                 multtemp.insert(0,j,0);
00925                 sumatemp += multtemp;
00926             }
00927             if (signo!=arg.signo) sumatemp.mC_B();
00928             return sumatemp;
00929         }
