00931         num_int operator / (const num_int & arg) const {
00932             num_int & cthis = *this;
00933             const sign_e sgn_ndo = signo;
00934             const sign_e sgn_sor = arg.signo;
00935             num_int dvndo_int(this->abs());
00936             dvndo_int.reduce();
00937             num_int rem(dvndo_int);
00938 
00939             num_int dvsor_int(arg.abs());
00940             dvsor_int.reduce();
00941 
00942             const usint cssor = dvsor_int.ceros_a_la_drcha(); //¿?
00943 
00944             num_int dvndo_fra(dvndo_int.substr(0,cssor));
00945             dvsor_int.lst_digs.erase(0,cssor);// division por una potencia de B
00946 
00947 
00948             dvsor_int.reduce();
00949             dvndo_int.reduce();
00950 
00951             dvndo_fra.reduce();
00952 
00953             const usint longtndo  = dvndo_int.size();
00954             const usint longtndo2 = dvndo_fra.size();
00955             const usint longtsor  = dvsor_int.size();
00956             const usint longsor   = longtsor;
00957 
00958             // hemos dividido por los ceros del divisor (por una potencia de B)
00959             // tanto divisor como dividendo, obteniendo dvndo_int y dvsor_int como
00960             // cocientes enteros.
00961             div_uno(rem,dvndo_int,dvsor_int,longtndo,longsor);
00962             // ahora hacemos lo mismo con el cociente de las potencias de B del
00963             // dividendo obteniendo dvndo_fra
00964 
00965             /* *
00966                 RemReal = RemObtenido*B^cssor + Dvndo_fra
00967 
00968                 rem = rem.lst_digs.insert(0,cssor,0);
00969                 rem += dvndo_fra;
00970             * */
00971             if (sgn_ndo!=sgn_sor) dvndo_int.mC_B();
00972             // rehacer el resto tomando en cuenta los ceros quitados
00973             // acomodar el formato al signo de la operacion
00974             // determinar el signo de la operacion
00975             return dvndo_int;
00976         }
