00978         num_int operator % (const num_int & arg) const {
00979             num_int & cthis = *this;
00980             num_int dvndo_int(this->abs());
00981             dvndo_int.reduce();
00982             num_int rem(dvndo_int);
00983 
00984             num_int dvsor_int(arg.abs());
00985             dvsor_int.reduce();
00986 
00987             const usint cssor = dvsor_int.ceros_a_la_drcha(); //¿?
00988 
00989             num_int dvndo_fra(dvndo_int.substr(0,cssor));
00990             dvsor_int.lst_digs.erase(0,cssor);// division por una potencia de B
00991 
00992 
00993             dvsor_int.reduce();
00994             dvndo_int.reduce();
00995 
00996             dvndo_fra.reduce();
00997 
00998             const usint longtndo  = dvndo_int.size();
00999             const usint longtndo2 = dvndo_fra.size();
01000             const usint longtsor  = dvsor_int.size();
01001             const usint longsor   = longtsor;
01002 
01003             // hemos dividido por los ceros del divisor (por una potencia de B)
01004             // tanto divisor como dividendo, obteniendo dvndo_int y dvsor_int como
01005             // cocientes enteros.
01006             div_uno(rem,dvndo_int,dvsor_int,longtndo,longsor);
01007             // ahora hacemos lo mismo con el cociente de las potencias de B del
01008             // dividendo obteniendo dvndo_fra
01009 
01010             /* *
01011                 RemReal = RemObtenido*B^cssor + Dvndo_fra
01012             * */
01013             rem = rem.lst_digs.insert(0,cssor,0);
01014             rem += dvndo_fra;
01015             // rehacer el resto tomando en cuenta los ceros quitados
01016             // acomodar el formato al signo de la operacion
01017             // determinar el signo de la operacion
01018             return rem;
01019         }
