01035         num_int operator * (pardigs a) const {
01036             num_int & cthis = *this;
01037             pardigs             temp;
01038             dig                         carry=0;
01039             num_int             ret(*this);
01040             for ( usint k=0 ; k < (cthis.size()-1) ; ++k ) {
01041                 temp = cthis[k]*(a.g_first());
01042                 temp += carry;
01043                 ret[k]= temp.g_first();
01044                 carry=temp.g_second();
01045             }
01046             if (carry != 0)
01047                 ret.insert((cthis.size())-1,1,carry);
01048             ret.reduce();
01049             carry = 0;
01050             for ( usint k=0 ; k < ((cthis.size())-1) ; ++k ) {
01051                 temp = cthis[k]*(a.g_second());
01052                 temp += carry;
01053                 ret.substr(k+1,ret.size()-1)+= temp.g_first();//¿?
01054                 carry=temp.g_second();
01055             }
01056             if (carry != 0)
01057                 ret.insert((cthis.size())-1,1,carry);
01058             ret.reduce();
01059 
01060             if (signo==vminus) ret.mC_B();
01061 
01062             return ret;
01063         }
