01068         num_int operator + (dig arg) const {
01069             num_int & cthis = *this;
01070             num_int ret(*this);
01071             ret.reduce();
01072             spardigs tempt();
01073             spardigs tempa();
01074             const num_int* po =0;
01075             const usint sza = 1;
01076             usint szt = ret.lst_arg.size();
01077             p_usint  pszmin=0;
01078             p_usint  pszmax=0;
01079             if (sza<=szt) {
01080                 pszmin = &sza;
01081                 pszmax = &szt;
01082                 po     = &ret;
01083             } else {
01084                 pszmin = &szt;
01085                 pszmax = &sza;
01086                 po     = &arg;
01087             }
01088 
01089             usint & szmin = *pszmin;
01090             usint & szmax = *pszmax;
01091             const num_int & obj = *po;
01092             usint k = 0;
01093             ret.aux = spardigs();
01094             tempt.p_first(ret[0]);
01095             tempa.p_first(arg);
01096 
01097             for ( k=1 ; k<szmin ; ++k) {
01098                 tempt.p_first(ret[k]);
01099                 tempt += tempa;
01100                 tempt += aux;
01101                 ret[k]=tempt.g_first();
01102                 ret.aux=spardigs(tempt.g_sign(),
01103                                  ((tempt.g_sign()==vplus)?(0):(B-1)),
01104                                  tempt.g_second()
01105                                 );
01106             }
01107             for ( ; k<szmax ; ++k) {
01108                 dig D = ret[k];
01109                 tempt.p_first(D);
01110                 tempt += aux;
01111                 ret[k]=tempt.g_first();
01112                 ret.aux=spardigs(tempt.g_sign(),
01113                                  ((tempt.g_sign()==vplus)?(0):(B-1)),
01114                                  tempt.g_second()
01115                                 );
01116             }
01117 
01118             ret.cthis.push_back(ret.aux.g_first());
01119             ret.cthis.push_back(ret.aux.g_second());
01120             ret.signo=ret.aux.g_sign();
01121             ret.aux =  spardigs();
01122             return ret;
01123         }
