01125         num_int operator - (dig arg) const {
01126             num_int & cthis = *this;
01127             num_int ret(*this);
01128             ret.reduce();
01129             spardigs cpy(-arg);
01130             spardigs tempt();
01131             spardigs tempa();
01132             usint szt = ret.lst_arg.size();
01133             usint k = 0;
01134             ret.aux = spardigs();
01135             tempt.p_first(ret[0]);
01136             tempa.p_first(arg.g_first());
01137             tempt += tempa;
01138             ret[0] =tempt.g_first();
01139             tempa = spardigs(vplus,0,tempt.g_second());
01140             tempt = spardigs(vplus,0,ret[1]);
01141             tempt += tempa;
01142             ret[1] = tempt.g_first();
01143 
01144             for ( k=2 ; k < ret.lst_digs.size() ; ++k) {
01145                 tempt.p_first(ret[k]);
01146                 tempt += aux;
01147                 ret[k]=tempt.g_first();
01148                 ret.aux=spardigs(tempt.g_sign(),
01149                                  ((tempt.g_sign()==vplus)?(0):(B-1)),
01150                                  tempt.g_second()
01151                                 );
01152             }
01153 
01154             ret.lst_digs.push_back(ret.aux.g_first());
01155             ret.lst_digs.push_back(ret.aux.g_second());
01156             ret.signo=ret.aux.g_sign();
01157             ret.aux =  spardigs();
01158             return ret;
01159         }
