01161         num_int operator * (dig a) const {
01162             num_int & cthis = *this;
01163             pardigs temp;
01164             dig         carry=0;
01165             num_int ret(*this);
01166             if (ret.signo==vminus) ret.mC_B();
01167             for ( usint k=0 ; k < (cthis.size()) ; ++k ) {
01168                 temp = cthis[k]*a;
01169                 temp += carry;
01170                 ret[k]= temp.g_first();
01171                 carry=temp.g_second();
01172             }
01173             if (carry != 0)
01174                 ret.insert((cthis.size())-1,1,carry);
01175             if (ret.signo==vminus) ret.mC_B();
01176             return ret;
01177         }
