01189         const num_int & operator += (const num_int &arg ) {
01190             num_int & cthis = *this;
01191             this->reduce();
01192             num_int carg(arg.reduce());
01193             spardigs tempt;
01194             spardigs tempa;
01195             const num_int*       po    =0;
01196             usint sza = carg.size();
01197             usint szt = cthis.size();
01198             p_usint pszmin=0;
01199             p_usint pszmax=0;
01200             if (sza<=szt) {
01201                 pszmin = &sza;
01202                 pszmax = &szt;
01203                 po     = this;
01204             } else {
01205                 pszmin = &szt;
01206                 pszmax = &sza;
01207                 po     = &carg;
01208             }
01209             const num_int & obj = (*po);
01210             tempt.p_decs(dig_t<B>(0));
01211             tempa.p_decs(0);
01212             usint & szmin = *pszmin;
01213             usint & szmax = *pszmax;
01214             usint k = 0;
01215             for ( ; k<szmin ; ++k ) {
01216                 tempt.p_uds(cthis[k]);
01217                 tempa.p_uds(carg[k]);
01218                 tempt += tempa;
01219                 cthis[k]=tempt.g_uds();
01220             }
01221             for (; k<szmax ; ++k) {
01222                 tempt.p_first(obj[k]);
01223                 tempt += (obj.aux);
01224                 cthis[k]=tempt.g_first();
01225             }
01226             aux.p_first(tempt.g_second());
01227             aux.p_second(0);
01228             this->reduce();
01229             return (*this);
01230         }
01231 
01232         const num_int & operator -= (const num_int & arg) {
01233             num_int & cthis = *this;
01234             this->reduce();
01235             num_int carg((-arg).reduce());
01236             spardigs tempt();
01237             spardigs tempa();
01238             const num_int*       po    =0;
01239             usint sza = carg.size();
01240             usint szt = (cthis.size());
01241             p_usint pszmin=0;
01242             p_usint pszmax=0;
01243             if (sza<=szt) {
01244                 pszmin = &sza;
01245                 pszmax = &szt;
01246                 po     = this;
01247             } else {
01248                 pszmin = &szt;
01249                 pszmax = &sza;
01250                 po     = &carg;
01251             }
01252             const num_int & obj = (*po);
01253             tempt.p_second(0);
01254             tempa.p_second(0);
01255             usint & szmin = *pszmin;
01256             usint & szmax = *pszmax;
01257             usint k = 0;
01258             for ( ; k<szmin ; ++k) {
01259                 tempt.p_first(cthis[k]);
01260                 tempa.p_first(carg[k]);
01261                 tempt += tempa;
01262                 cthis[k]=tempt.g_first();
01263             }
01264             for (; k<szmax ; ++k) {
01265                 tempt.p_first(obj.lst_digs[k]);
01266                 tempt += (obj.aux);
01267                 cthis[k]=tempt.g_first();
01268             }
01269             aux.p_first(tempt.g_second());
01270             aux.p_second(0);
01271             this->reduce();
01272             return (*this);
01273         }
