01275         const num_int & operator *= (const num_int & arg) {
01276             num_int & cthis = *this;
01277             num_int multtemp;
01278             const sign_e sgn_ndo = signo;
01279             this->absp();
01280             this->reduce();
01281             num_int & m1 = (*this);
01282             num_int m2(arg.abs());
01283             m2.reduce();
01284 
01285             cthis *= m2[0];
01286             for (usint j = 1 ; j < m2.size() ; ++j) {
01287                 multtemp = m1*m2[j];
01288                 multtemp.insert(0,j,0);
01289                 cthis += multtemp;
01290             }
01291             if (sgn_ndo!=arg.signo) m1.mC_B();
01292             return (*this);
01293         }
01294 
01295         const num_int & operator /= (const num_int & arg) {
01296             num_int & cthis = *this;
01297             const sign_e sgn_ndo = signo;
01298             const sign_e sgn_sor = arg.signo;
01299             this->absp();
01300             num_int & dvndo_int = *this;
01301             dvndo_int.reduce();
01302             num_int rem(dvndo_int);
01303 
01304             num_int dvsor_int(arg.abs());
01305             dvsor_int.reduce();
01306 
01307             const usint cssor = dvsor_int.ceros_a_la_drcha(); //¿?
01308 
01309             num_int dvndo_fra(dvndo_int.substr(0,cssor));
01310             dvsor_int.lst_digs.erase(0,cssor);// division por una potencia de B
01311 
01312 
01313             dvsor_int.reduce();
01314             dvndo_int.reduce();
01315 
01316             dvndo_fra.reduce();
01317 
01318             const usint longtndo  = dvndo_int.size();
01319             const usint longtndo2 = dvndo_fra.size();
01320             const usint longtsor  = dvsor_int.size();
01321             const usint longsor   = longtsor;
01322 
01323             // hemos dividido por los ceros del divisor (por una potencia de B)
01324             // tanto divisor como dividendo, obteniendo dvndo_int y dvsor_int como
01325             // cocientes enteros.
01326             div_uno(rem,dvndo_int,dvsor_int,longtndo,longsor);
01327             // ahora hacemos lo mismo con el cociente de las potencias de B del
01328             // dividendo obteniendo dvndo_fra
01329 
01330             /* *
01331                 RemReal = RemObtenido*B^cssor + Dvndo_fra
01332 
01333                 rem = rem.lst_digs.insert(0,cssor,0);
01334                 rem += dvndo_fra;
01335             * */
01336             if (sgn_ndo!=sgn_sor) dvndo_int.mC_B();
01337             // rehacer el resto tomando en cuenta los ceros quitados
01338             // acomodar el formato al signo de la operacion
01339             // determinar el signo de la operacion
01340             return dvndo_int;
01341         }
