01343         const num_int & operator %= (const num_int & arg) {
01344             num_int & cthis = *this;
01345             (*this).absp();
01346             num_int & rem = *this;
01347             rem.reduce();
01348             num_int dvndo_int(rem);
01349 
01350             num_int dvsor_int(arg.abs());
01351             dvsor_int.reduce();
01352 
01353             const usint cssor = dvsor_int.ceros_a_la_drcha(); //¿?
01354 
01355             num_int dvndo_fra(dvndo_int.substr(0,cssor));
01356             dvsor_int.lst_digs.erase(0,cssor);// division por una potencia de B
01357 
01358 
01359             dvsor_int.reduce();
01360             dvndo_int.reduce();
01361 
01362             dvndo_fra.reduce();
01363 
01364             const usint longtndo  = dvndo_int.size();
01365             const usint longtndo2 = dvndo_fra.size();
01366             const usint longtsor  = dvsor_int.size();
01367             const usint longsor   = longtsor;
01368 
01369             // hemos dividido por los ceros del divisor (por una potencia de B)
01370             // tanto divisor como dividendo, obteniendo dvndo_int y dvsor_int como
01371             // cocientes enteros.
01372             div_uno(rem,dvndo_int,dvsor_int,longtndo,longsor);
01373             // ahora hacemos lo mismo con el cociente de las potencias de B del
01374             // dividendo obteniendo dvndo_fra
01375 
01376             /* *
01377                 RemReal = RemObtenido*B^cssor + Dvndo_fra
01378             * */
01379 
01380             rem = rem.lst_digs.insert(0,cssor,0);
01381             rem += dvndo_fra;
01382 
01383             return rem;
01384         }
