01394         const num_int & operator *= (pardigs a) {
01395             num_int & cthis = *this;
01396             pardigs             temp;
01397             dig                         carry = 0;
01398             num_int &           ret = (*this);
01399             for ( usint k=0 ; k < cthis.size() ; ++k ) {
01400                 temp = cthis[k]*(a.g_first());
01401                 temp += carry;
01402                 ret[k]= temp.g_first();
01403                 carry=temp.g_second();
01404             }
01405             if (carry != 0)
01406                 ret.insert((cthis.size())-1,1,carry);
01407             ret.reduce();
01408             carry = 0;
01409             for ( usint k=0 ; k < ((cthis.size())-1) ; ++k ) {
01410                 temp = cthis[k]*(a.g_second());
01411                 temp += carry;
01412                 ret.substr(k+1,ret.size()-1)+= temp.g_first();//¿?
01413                 carry=temp.g_second();
01414             }
01415             if (carry != 0)
01416                 ret.insert((cthis.size())-1,1,carry);
01417             ret.reduce();
01418 
01419             if (signo==vminus) ret.mC_B();
01420 
01421             return ret;
01422         }
