01427         const num_int & operator += (dig arg) {
01428             num_int & cthis = *this;
01429             this->reduce();
01430             spardigs tempt();
01431 
01432             tempt.p_second(0);
01433             usint k = 1;
01434             tempt.p_first(cthis[0]);
01435             tempt += spardigs(arg);
01436             cthis[0]=tempt.g_first();
01437             aux = spardigs(tempt.g_second());
01438             for (; k < cthis.size() ; ++k) {
01439                 tempt.p_first(0);
01440                 tempt += aux;
01441                 cthis[k]=tempt.g_first();
01442                 aux = spardigs(tempt.g_second());
01443             }
01444             aux.p_first(tempt.g_second());
01445             aux.p_second(0);
01446             this->reduce();
01447             return (*this);
01448         }
