01450         const num_int & operator -= (dig arg) {
01451             num_int & cthis = *this;
01452             this->reduce();
01453             dig arg_CB = (dig(B-1)-arg)+dig(1);
01454             spardigs tempt();
01455 
01456             tempt.p_second(0);
01457             usint k = 1;
01458             tempt.p_first(cthis[0]);
01459             tempt += arg_CB;
01460             cthis[0]=tempt.g_first();
01461             aux = spardigs(tempt.g_second());
01462             if (arg==0) aux += dig(B-1);
01463             for (; k < cthis.size() ; ++k) {
01464                 tempt.p_first(0);
01465                 tempt += aux;
01466                 cthis[k]=tempt.g_first();
01467                 aux = spardigs(tempt.g_second());
01468             }
01469             aux.p_first(tempt.g_second());
01470             aux.p_second(0);
01471             this->reduce();
01472             return (*this);
01473         }
