01475         const num_int & operator *= (dig arg) {
01476             num_int & cthis = *this;
01477             pardigs temp;
01478             dig         carry=0;
01479             sign_e  sgn = this->signo;
01480             this->absp();
01481             this->reduce();
01482             for ( usint k=0 ; k < (cthis.size()) ; ++k ) {
01483                 temp = cthis[k]*arg;
01484                 temp += carry;
01485                 cthis[k]= temp.g_first();
01486                 carry=temp.g_second();
01487             }
01488             if (carry != 0)
01489                 insert((cthis.size())-1,1,carry);
01490             if (sgn==vminus)
01491                 this->mC_B();
01492             return (*this);
01493         }
