01505         num_int operator -() const {
01506             num_int & cthis = *this;
01507             num_int cpy(*this);
01508             cpy.reduce();
01509             const striterator fin = cpy.end();
01510             striterator  it = cpy.begin();
01511             for ( ; it != fin ; ++it )
01512                 (*it) = (!(*it));
01513 
01514             it = cpy.begin();
01515             cpy.aux = (*it)+1;
01516             cpy[0]=cpy.aux.g_first();
01517             ++it;
01518             if ((cpy.aux.g_second())!= spardigs()) {
01519                 for ( ; it != fin ; ++it ) {
01520                     cpy.aux.p_first(cpy.aux.g_second());
01521                     cpy.aux.p_second(0);
01522                     if (cpy.aux == spardigs()) break;
01523                     cpy.aux += spardigs(*it);
01524                 }
01525                 cpy.push_back(cpy.aux.g_first());
01526                 cpy.push_back(cpy.aux.g_second());
01527             }
01528             cpy.signo = ((cpy.signo==vminus)?vplus:vminus);
01529             cpy.aux = spardigs();
01530             cpy.reduce();
01531             return cpy;
01532         }
01533 
01534         num_int operator !() const {
01535             num_int & cthis = *this;
01536             num_int cpy(*this);
01537             const striterator fin = cpy.end();
01538             striterator  it = cpy.begin();
01539             for ( ; it != fin ; ++it )
01540                 (*it) = (!(*it));
01541             cpy.signo = ((cpy.signo==vminus)?vplus:vminus);
01542             cpy.aux = spardigs();
01543             cpy.reduce();
01544             return cpy;
01545         }
