01551                                                         /*              BASE MENOS 1                        */
01552                                                         /*                                                                              */
01553                                                         /***********************************/
01554 
01555         const num_int & mC_B() {
01556             num_int & cthis = *this;
01557             cthis.reduce();
01558             const striterator fin = cthis.end();
01559             striterator  it = cthis.begin();
01560             for ( ; it != fin ; ++it )
01561                 (*it) = (!(*it));
01562             it = cthis.begin();
01563             aux = (*it)+1;
01564             cthis[0]=aux.g_first();
01565             ++it;
01566             if ((aux.g_second())!= spardigs()) {
01567                 for ( ; it != fin ; ++it ) {
01568                     aux.p_first(aux.g_second());
01569                     aux.p_second(0);
01570                     if (aux == spardigs()) break;
01571                     aux += spardigs(*it);
01572                 }
01573                 push_back(aux.g_first());
01574                 push_back(aux.g_second());
01575             }
01576             signo = ((signo==vminus)?vplus:vminus);
01577             aux = spardigs();
01578             cthis.reduce();
01579             return cthis;
01580         }
