01584             num_int & cpy = (*this);
01585             const striterator fin = cpy.end();
01586             striterator  it = cpy.begin();
01587             for ( ; it != fin ; ++it )
01588                 (*it) = (!(*it));
01589             cpy.signo = ((cpy.signo==vminus)?vplus:vminus);
01590             cpy.aux = spardigs();
01591             cpy.reduce();
01592             return cpy;
01593         }
