01601         num_int abs() const {
01602             const num_int cthis(*this);
01603             cthis.reduce();
01604             if (signo==vminus)
01605                 cthis.mC_B();
01606             cthis.reduce();
01607             return cthis;
01608         }
01609 
01610         const num_int & absp() {
01611             num_int & cthis = (*this);
01612             cthis.reduce();
01613             if (signo==vminus) cthis.mC_B();
01614             cthis.reduce();
01615             return cthis;
01616         }
