01626             cpy.reduce();
01627             sign_e sgn = cpy.signo;
01628             cpy.absp();
01629             cpy.cthis.erase(0,1);
01630             if (sgn==vminus) cpy.mC_B();
01631             cpy.reduce();
01632             return cpy;
