01673             if (B==2) dvsor_int[0]=1;// division por una potencia de B
01674 
01675             dvndo_int.reduce();
01676             dvndo_fra.reduce();
01677 
01678             const usint longtndo  = dvndo_int.size();
01679             const usint longtndo2 = dvndo_fra.size();
01680             const usint longtsor  = 1;
01681             const usint longsor   = 0;
01682 
01683             // hemos dividido por los ceros del divisor (por una potencia de B)
01684             // tanto divisor como dividendo, obteniendo dvndo_int y dvsor_int como
01685             // cocientes enteros.
01686             div_uno(rem,dvndo_int,dvsor_int,longtndo,longsor);
01687             // ahora hacemos lo mismo con el cociente de las potencias de B del
01688             // dividendo obteniendo dvndo_fra
