01691                 RemReal = RemObtenido*B^cssor + Dvndo_fra
01692 
01693                 rem = rem.cthis.insert(0,cssor,0);
01694                 rem += dvndo_fra;
01695             * */
01696             if (sgn==vminus) dvndo_int.mC_B();
01697             // rehacer el resto tomando en cuenta los ceros quitados
01698             // acomodar el formato al signo de la operacion
01699             // determinar el signo de la operacion
