01703         dig rem2 () const {
01704             num_int & cthis = *this;
01705             sign_e sgn = signo;
01706             num_int cpy = cthis.reduce();
01707             cpy.absp();
01708             cpy.reduce();
01709             if (B%2==1) {
01710                 if (cpy.lst_digs.size()==1)
01711                     return dig::Rem2(cpy.lst_digs[0]);
01712                 else {
01713                     usint paridad = 0;
01714                     for ( usint k=0 ; k < cpy.lst_digs.size() ; ++k )
01715                         paridad += dig::Dig2UInt(dig::Rem2(cpy.lst_digs[k]));
01716                     paridad %= 2;
01717                     return dig(paridad);
01718                 }
01719             }
01720             else {
01721                 return dig::Rem2(cpy.lst_digs[0]);
01722             }
01723         }
