01769         dig rem2p() {
01770             num_int & cthis = *this;
01771             sign_e sgn = signo;
01772             cthis.absp();
01773             cthis.reduce();
01774             num_int & cpy = (*this);
01775             if (B%2==1) {
01776                 if (cpy.size()==1)
01777                     return dig::Rem2(cpy.lst_digs[0]);
01778                 else {
01779                     usint paridad = 0;
01780                     for ( usint k=0 ; k < cpy.size() ; ++k )
01781                         paridad += dig::Dig2UInt(dig::Rem2(cpy[k]));
01782                     paridad %= 2;
01783                     return dig(paridad);
01784                 }
01785             }
01786             else {
01787                 return dig::Rem2(cpy[0]);
01788             }
01789         }
