01798                         num_int div7 () const;
01799                         dig rem7 () const;
01800                         const num_int & div7p();
01801                         dig rem7p();
01802                         const dig rem8() const;
01803         */
01804 
01805         dig                     remBm1()        const {
01806             num_int cthis(*this),suma;
01807             sign_e sgn = signo;
01808             cthis.abs();
01809             cthis.reduce();
01810             suma.reduce();
01811             suma.lst_digs.insert(0,1,0);
01812             suma.lst_digs.erase(0,(cthis.size())-1);
01813             do {
01814                 for ( usint i = 0 ; i < cthis.size() ; ++i ) {
01815                     suma += cthis[i];
01816                     suma.reduce();
01817                 }
01818             } while (suma.size()>1);
01819 
01820             if ((suma[0]==0)||(suma[0]==(B-1))) return 0;
01821             else return suma[0];
01822         }
