01842         inline void     div_gen_step(const num_int & dvsor, num_int & rem, num_int & coc, usint & ndig) const {
01843             num_int & cthis = *this;
01844             const usint maxpos = (cthis.size())-1;
01845             const num_int & dvndo = (*this);
01846             rem.insert(0,1,dvndo[maxpos-ndig]);
01847             if (rem < coc) {
01848                 coc.insert(0,1,0);
01849             }
01850             else if (rem == coc) {
01851                 coc.insert(0,1,1);
01852                 rem.erase(0,rem.size()-1);
01853                 rem[0]=0;
01854             }
01855             else {
01856                 if (rem.size() > dvsor.size()) {
01857                     pardigs dighrem        = pardigs(rem[rem.size()-1],rem[rem.size()-2]) ;
01858                     pardigs dighsor        = pardigs(0,dvsor[dvsor.size()-1]) ;
01859                     pardigs & digcocprueba = dighrem ;
01860                     digcocprueba /= dighsor ;
01861                     num_int remprueba = dvsor*digcocprueba ;
01862                     for ( dig_t<B> ix = 0  ; ix < pardigs(1,0) ; ix += 1 ) {
01863                         if ( remprueba > rem ) {
01864                             --digcocprueba;
01865                             remprueba -= dvsor;
01866                         }
01867                         else {
01868                             rem -= remprueba;
01869                             coc.insert(0,1,digcocprueba);
01870                             break;
01871                         }
01872                     }
01873                 }
01874                 else {
01875                     dig_t<B> dighrem        = rem[rem.size()-1];
01876                     dig_t<B> dighsor        = dvsor[dvsor.size()-1];
01877                     dig_t<B> & digcocprueba = dighrem;
01878                     digcocprueba /= dighsor;
01879                     num_int remprueba = dvsor*digcocprueba; // no está implementada esta función
01880                     dig_t<B> ix = 0 ;
01881                     for (  ; ix < pardigs(1,0) ; ix += 1 ) {
01882                         if ( remprueba > rem ) {
01883                             --digcocprueba;
01884                             remprueba -= dvsor;
01885                         }
01886                         else {
01887                             rem -= remprueba;
01888                             coc.insert(0,1,digcocprueba);
01889                             break;
01890                         }
01891                     }
01892                 }
01893             }
01894             ++ndig;
01895             return;
01896         }
