01904                 coc.insert(0,1,0);
01905             }
01906             else if (rem == coc) {
01907                 coc.insert(0,1,1);
01908                 rem.erase(0,rem.size()-1);
01909                 rem[0]=0;
01910             }
01911             else {
01912                 if (rem.size() > dvsor.size()) {
01913                     pardigs dighrem        = pardigs(rem[rem.size()-1],rem[rem.size()-2]);
01914                     const pardigs dighsor  = pardigs(0,dvsor[dvsor.size()-1]);
01915                     pardigs & digcocprueba = dighrem;
01916                     digcocprueba /= dighsor;
01917                     num_int remprueba = dvsor*digcocprueba;
01918                     for ( dig_t<B> Id = 0 ; Id < pardigs(1,0) ; Id+=1 ) {
01919                         if ( remprueba > rem ) {
