01960         inline void div_uno( num_int & rem , num_int & dvndo, num_int & dvsor, const usint long_dvndo, const usint long_dvsor) const {
01961             num_int & cthis = *this;
01962             rem = dvndo;
01963             if ((long_dvsor==0)||((long_dvsor==1)&&(dvsor[0]=0))) { // caso de division por 0
01964                 return;
01965             }
01966             else if ((long_dvndo==0)||(long_dvndo < long_dvsor)) { // caso de 0 dividido por !=0 ó caso de cociente 0
01967                 rem = dvndo;
01968                 dvndo.erase(0,(cthis.size())-1);
01969                 dvndo[0]=0;
01970                 return;
01971                 // rem=dvndo; hecho por defecto
01972             }
01973             else if (dvndo==dvsor) { // caso de dividir por si mismo
01974                 dvndo.erase(0,(cthis.size())-1);
01975                 dvndo[0]=1;
01976                 rem.erase(0,rem.size()-1);
01977                 rem[0]=0;
01978                 return;
01979             }
01980             else if ((long_dvsor==1)&&(dvsor[0]==1)) { // caso de dividir por potencias de B
01981                 //dvndo;
01982                 rem.erase(0,rem.size()-1);
01983                 rem[0]=0;
01984             }
01985             /*          else if ((long_dvsor==1)&&((dvsor[0]==2)||(dvsor[0]==4)||(dvsor[0]==8))) {
01986                         // caso de dividir por potencias de B multiplicadas por 2, 4 u 8
01987                                 //dvndo;
01988                                 switch(dvsor[0]) {
01989                                         case 2  : rem=num_int(rem.rem2()); break;
01990                                         case 4  : rem=num_int(rem.rem4()); break;
01991                                         case 8  :
01992                                         default : rem=num_int(rem.rem8());
01993                                 }
01994                                 dvndo.div2p();
01995                                 switch(dvsor[0]) {
01996                                         case 4:  dvndo.div2p(); break;
01997                                         case 8:  dvndo.div2p(); dvndo.div2p();
01998                                 }
01999 
02000                         }
02001                         else if ((long_dvsor==1)&&((dvsor[0]==3)||(dvsor[0]==9))) {
02002                         // caso de dividir por potencias de B multiplicadas por 3 u 9
02003                                 //dvndo;
02004                                 switch(dvsor[0]) {
02005                                         case 3  : rem=num_int(rem.rem3()); break;
02006                                         case B-1  :
02007                                         default : rem=num_int(rem.rem9()); break;
02008                                 }
02009                                 dvndo.div3p();
02010                                 if(dvsor[0]==n) dvndo.div3p();
02011                         }
02012                         else if ((long_dvsor==1)&&(dvsor[0]==6)) {
02013                         // caso de dividir por potencias de B multiplicadas por 6
02014                                 //dvndo;
02015                                 rem.rem6();
02016                                 dvndo.div2p();
02017                                 dvndo.div3p();
02018                         }
02019                         else if ((long_dvsor==1)&&(dvsor[0]==5)) {
02020                         // caso de dividir por potencias de B multiplicadas por 5
02021                                 rem.rem5();
02022                                 if (long_dvndo > 1) {
02023                                         dvndolst_digs.erase(0,1);
02024                                         // Divido por B
02025                                         dvndo *= d;
02026                                         // y he multiplicado por 2 (*2/B ó *1/5)
02027                                 }
02028                                 else {
02029                                         //dvndo;
02030                                         //dvndolst_digs.erase(0,(cthis.size())-1);
02031                                         switch (dvndo[0]) {
02032                                                 case 0 :
02033                                                 case 1 :
02034                                                 case 2 :
02035                                                 case 3 :
02036                                                 case 4 :  dvndo[0] = 0; break;
02037                                                 default : dvndo[0] = 1;
02038                                         }
02039                                 }
02040                         }
02041                         else if ((long_dvsor==1)&&(dvsor[0]==7)) {
02042                         // caso de dividir por potencias de B multiplicadas por 7
02043                                 //dvndo;
02044                                 rem.rem7();
02045                                 dvndo.div7();
02046                         }*/
02047             else if ((long_dvndo == long_dvsor) && (dvndo < dvsor)) {
02048                 rem = dvndo;
02049                 dvndo.erase(0,(cthis.size())-1);
02050                 dvndo[0]    =0;
02051             }
02052             else {
02053                 // algoritmo de resta general
02054                 // implementar div_step pasamos (resto,cociente,i,dividendo,divisor) devuelve (resto,cociente,i,dividendo,divisor)
02055                 // se puede hacer con una tupla
02056                 //dvndo;
02057                 num_int rem   = (dvndo = dvndo.substr((cthis.size())-1-long_dvsor,long_dvsor));
02058                 num_int coc(0);
02059                 usint ndig = 0;
02060                 do {
02061                     div_gen_step(dvsor,rem,coc,ndig);
02062                 } while ( rem >= dvsor );
02063                 dvndo = coc;
02064                 return;
02065             }
02066         }
